import React, { useState, useEffect } from 'react';

const testImageUrl = async (url) => {
  try {
    const response = await fetch(url, { method: 'HEAD' });
    return response.ok;
  } catch (error) {
    return false;
  }
};

const getWorkingIpfsUrl = async (cid) => {
  const gateways = [
    `https://ipfs.io/ipfs/${cid}`,
    `https://cloudflare-ipfs.com/ipfs/${cid}`,
    `https://gateway.pinata.cloud/ipfs/${cid}`
  ];

  for (const gateway of gateways) {
    const isWorking = await testImageUrl(gateway);
    if (isWorking) {
      return gateway;
    }
  }

  return null;
};

const Results = ({ content }) => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    const fetchImages = async () => {
      if (content) {
        const output = content["output"];
        const imagesWithUrls = await Promise.all(
          output.map(async (item) => {
            const cid = item[2].split('/').pop(); // Extract CID from the URL
            const workingUrl = await getWorkingIpfsUrl(cid);
            return { ...item, workingUrl };
          })
        );
        setImages(imagesWithUrls);
      }
    };

    fetchImages();
  }, [content]);

  if (content === null) {
    return (
      <p>null</p>
    )
  } else {
    try {
      var output = content["output"];
      console.log(output)
      return (
        <div className="grid-container">
          {output.map((item, index) => (
            <div className="grid-item" key={index}>
              <div id="image-container">
                <img src={item.workingUrl || item[2]} alt={item[0]} />
              </div>
              <h3>{item[0] ? item[0] : "<null>"}</h3>
              <p>{item[1]}</p>
              <a href={item[3]} target="_blank" rel="noopener noreferrer">View Metadata</a>
            </div>
          ))}
        </div>
      );
    } catch {
      return (
        <p>error</p>
      )
    }
  }
};


export default Results;
