  import React, { useEffect, useState } from 'react';
  import axios from 'axios';
  import { useNavigate } from 'react-router-dom';
  import { getToken, isAuthenticated } from '../utils/auth';
  import "./account.css"

  const Account = () => {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();
    const [content, setContent] = useState('home');

    useEffect(() => {
      if (!isAuthenticated()) {
        navigate('/login');
        return;
      }

      const fetchUserData = async () => {
        try {
          const token = getToken();
          const response = await axios.get('http://localhost:5000/api/account', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setUser(response.data);
        } catch (error) {
          console.error('Failed to fetch user data', error);
          navigate('/login');
        }
      };

      fetchUserData();
    }, [navigate]);

    if (!user) return <div>Loading...</div>;

    const RenderContent = () => {
      switch (content) {
        case 'dashboard':
          return <Dashboard />;
        case 'owned-nfts':
          return <Owned_NFTs />;
        case 'favourites':
          return <Favourites />;
        case 'settings':
          return <Settings />;
        default:
          return <Dashboard />;
      }
    };

    return (
      <main>
        <div class="account-header">
          <h1 class='account-info'>Welcome, {user ? user.username : '<undefined>'}!</h1> {/* Display username */}
          <button class='account-buttons' onClick={() => setContent('dashboard')}>Account Dashboard</button>
          <button class='account-buttons' onClick={() => setContent('owned-nfts')}>Owned NFTs</button>
          <button class='account-buttons' onClick={() => setContent('favourites')}>Favourites</button>
          <button class='account-buttons' onClick={() => setContent('settings')}>Settings</button>
        </div>
        <div class="content">
          <RenderContent />
        </div>
      </main>
    );
  };

  const Dashboard = () => (
    <div>
      <h2>Dashboard</h2>
      <p>This is the Dashboard content.</p>
    </div>
  );

  const Owned_NFTs = () => (
    <div>
      <h2>Owned NFTs</h2>
      <p>This is the Owned NFTs content.</p>
    </div>
  );

  const Favourites = () => (
    <div>
      <h2>Favourites</h2>
      <p>This is the Favourites content.</p>
    </div>
  );

  const Settings = () => (
    <div>
      <h2>Settings</h2>
      <p>This is the Settings content.</p>
    </div>
  );

  export default Account;
