// src/components/Account.js

import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { decodeToken } from '../utils/auth';

const Account = () => {
  const { user, isAuthenticated, getAccessTokenSilently } = useAuth0();

  const [decodedToken, setDecodedToken] = React.useState(null);

  React.useEffect(() => {
    const getToken = async () => {
      if (isAuthenticated) {
        const token = await getAccessTokenSilently();
        const decoded = decodeToken(token);
        setDecodedToken(decoded);
      }
    };

    getToken();
  }, [isAuthenticated, getAccessTokenSilently]);

  return (
    isAuthenticated && (
      <div className="account-header">
        <h1 className="account-info">Welcome, {decodedToken ? decodedToken.name : '<undefined>'}!</h1> {/* Display username */}
        <button className="account-buttons">Account Dashboard</button>
        <button className="account-buttons">Owned NFTs</button>
        <button className="account-buttons">Favourites</button>
      </div>
    )
  );
};

export default Account;
